import styled from 'styled-components';

// Header Area
export const FooterArea = styled.div`
    margin-top : 50px;
    min-height : 5vh;
    background : #323228;
`
export const FooterCopy = styled.p`
    color : #fff;
    font-size : .9em;
    text-align : center;
    line-height : 5vh;

    b{
        font-weight : bold;
    }
`
