import React , {useState} from "react";
import { BrowserRouter, Link } from "react-router-dom";
import { HeaderArea, HeaderLogo, HeaderMainMenu } from "../../assets/style/layout/headerStyle"
import HeaderMenu from "../../components/layout/HeaderMenu";
import headerLogo from "../../assets/images/default/blog-logo.svg"


const Header = () => {
	const [layerOpen, setLayerOpen] = useState(false);	
	const [layerBtn, setBtnActive] = useState(false);

	const toggleMenu = () => {		
        setLayerOpen(isOpen => !isOpen);
    }	

	const closeMenu = () => {		
        setLayerOpen(false);
    }		

	const toggleActive = () => {
	  setBtnActive((prev) => {
		return !prev;
	  });
	};

    return (
        <HeaderArea className="header" id="header">
            <HeaderLogo className="header-logo"><Link to="/"><img src={headerLogo} alt="logo" /></Link></HeaderLogo>
            <HeaderMainMenu type="button" className="header-main-menu" onClick={()=>{toggleMenu()}}><span></span><span></span></HeaderMainMenu>  

            <div className="header-all-menu">
                <button type="button" className="remove"><span></span><span></span></button>
                <HeaderMenu />
            </div>
        </HeaderArea>
    );
}

export default Header;
